mapper.select:
SELECT count(*)
  FROM likes
 WHERE artwork_id = #{artworkId} AND member_id = #{memberId}

mapper.insert:
INSERT INTO likes (
    like_id,
    artwork_id,
    member_id
)
VALUES (
    DEFAULT,
    #{artworkId},
    #{memberId}
)

mapper.delete:
DELETE
  FROM likes
 WHERE artwork_id = #{artworkId} AND member_id = #{memberId}

mapper.select:
SELECT count(*)
  FROM likes
 WHERE artwork_id = #{artworkId}

mapper.update:
UPDATE artwork
   SET like_count = like_count + 1
 WHERE artwork_id = #{artworkId}

UPDATE artwork
   SET like_count = like_count - 1
 WHERE artwork_id = #{artworkId}

UPDATE preference
   SET prefer_level = prefer_level + 1
 WHERE member_id = #{memberId}
       AND category_id IN (SELECT category_id
                             FROM artwork_category
                            WHERE artwork_id = #{artworkId});

UPDATE preference
   SET prefer_level = prefer_level - 1
 WHERE member_id = #{memberId}
       AND category_id IN (SELECT category_id
                             FROM artwork_category
                            WHERE artwork_id = #{artworkId});
